import React, { useState } from 'react';

const HookCounter = () => {
    const [count, handleIncrement] = useState(0);

    const incrementByFive=()=>{
        for(let i = 1;i < 5; i++){
            handleIncrement(PreviousCount=>PreviousCount + 1)
        }
    }

    return (
        <div>
            {count < 10 ? <button onClick={() => handleIncrement(count + 1)} >+ {count}</button> :
                <button onClick={() => handleIncrement(count - 1)} > - {count}</button>}

    <button onClick={incrementByFive} >IncrementByFive{count}</button>
        </div>
    )
}

export default HookCounter;
