import React, { useState } from 'react';

const HookCounter = () => {
    const [count, handleIncrement] = useState(0);

    return (
        <div>
            {count < 10 ? <button onClick={() => handleIncrement(count + 1)} >+ {count}</button> :
                <button onClick={() => handleIncrement(count - 1)} > - {count}</button>}


        </div>
    )
}

export default HookCounter;
