import React, { useState } from 'react';

const HookCounter = () => {
    const [count, setIncrement] = useState(0);
    const [showAlert, setShowAlert] = useState(false);

    const incrementByFive = () => {
        setIncrement(count + 1)
    }

    const drecrement = () => {

        if (count > 2) {
            return setShowAlert(!showAlert) 
        }

    }

    return (
        <div>
            {!showAlert ? alert('You reached maximum limit please Decrese your count') : ''}
            <button onClick={count < 5 ? incrementByFive : drecrement} >IncrementByFive{count}</button>
        </div>
    )
}

export default HookCounter;
